var SPRITE_GROSSINI_TAG = 1;
var SPRITE_TAMARA_TAG = 2;
var SPRITE_KATHIA_TAG = 3;

var ActionSequenceTest = cc.LayerGradient.extend({
    init : function(){
        this._super(cc.c4b(0,0,0,255), cc.c4b(98,99,117,255) );

        this._grossini = cc.Sprite.create(res.grossini_png);
        this.addChild(this._grossini, SPRITE_GROSSINI_TAG);
        winSize = cc.Director.getInstance().getWinSize();
        this._grossini.setPosition(60, winSize.height / 2);

        var action = cc.Sequence.create(
            cc.MoveBy.create(1.5, cc.p(240, 0)),
            cc.RotateBy.create(1.5, 540)
        );

        this._grossini.runAction(action);
        
        return true;
    }
});
ActionSequenceTest.create = function(args){
    var layer = new ActionSequenceTest();
    layer.init();
    return layer;
}