var FlipXTest = Actions3dTestLayer.extend({
    getEffect : function(duration){
        var a = cc.FlipX3D.create(duration );
        var delay = cc.DelayTime.create(2);
        var r = a.reverse();
        return cc.Sequence.create( a, delay, r );
    }
});
FlipXTest.create = function(args){
    var layer = new FlipXTest();
    layer.init();
    return layer;
}