var Lens3DTest = Actions3dTestLayer.extend({
    getEffect : function(duration){
        return cc.Lens3D.create( duration, cc.size(15,10), cc._p(winSize.width/2, winSize.height/2), 240);
    }
});
Lens3DTest.create = function(args){
    var layer = new Lens3DTest();
    layer.init();
    return layer;
}