var S9SpriteFromBatchNodeTest = GuiTest.extend({

    ctor:function() {
        this._super();

        var winSize = cc.Director.getInstance().getWinSize();
        var x = winSize.width / 2;
        var y = winSize.height / 2;

        var batchNode_scaled_with_insets = cc.SpriteBatchNode.create(res.blocks9_png);
        logTest("batchNode_scaled_with_insets created with : blocks9.png");

        var blocks_scaled_with_insets = cc.Scale9Sprite.create();
        logTest("Scale9Sprite created");

        blocks_scaled_with_insets.updateWithBatchNode(batchNode_scaled_with_insets, cc.rect(0, 0, 96, 96), false, cc.rect(32, 32, 32, 32));
        logTest("Scale9Sprite updateWithBatchNode");

        blocks_scaled_with_insets.setContentSize(96 * 4.5, 96 * 2.5);
        logTest("... setContentSize");

        blocks_scaled_with_insets.setPosition(x, y);
        logTest("... setPosition");

        this.addChild(blocks_scaled_with_insets);

        logTest("... S9BatchNodeScaleWithCapInsets done.");
    }
});

S9SpriteFromBatchNodeTest.create = function() {
    return new S9SpriteFromBatchNodeTest();
}