var S9SpriteFromSpriteSheetTest = GuiTest.extend({

    ctor:function() {
        this._super();

        var winSize = cc.Director.getInstance().getWinSize();
        var x = winSize.width / 2;
        var y = winSize.height / 2;

        // Add sprite frames with a plist file, it has to be preloaded
        cc.SpriteFrameCache.getInstance().addSpriteFrames(res.blocks9ss_plist);
        // Create Scale9Sprite with a frame name
        var blocks = cc.Scale9Sprite.createWithSpriteFrameName('blocks9r.png');
        blocks.setPosition(x, y);
        this.addChild(blocks);

        var label = cc.LabelTTF.create(this._title, "Arial", 28);
        this.addChild(label, 1);
        label.setPosition(winSize.width / 2, winSize.height - 50);
    }
});

S9SpriteFromSpriteSheetTest.create = function() {
    return new S9SpriteFromSpriteSheetTest();
}