var KazmathTest = cc.Layer.extend({
    init : function(){
        this._super();
        var label = cc.LabelTTF.create("No test case to display!")
        var winSize = cc.Director.getInstance().getWinSize();
        this.addChild(label);
        label.setPosition(winSize.width/2, winSize.height/2);
        return true;
    }
});
KazmathTest.create = function(args){
    var layer = new KazmathTest();
    layer.init();
    return layer;
};