var FlowerTest = ParticleTest.extend({
    onEnter:function () {
        this._super();

        // Create particle emitter
        this._emitter = cc.ParticleFlower.create();
        this._background.addChild(this._emitter, 10);
        // Set particle texture
        var myTexture = cc.TextureCache.getInstance().addImage(res.stars_png);
        this._emitter.setTexture(myTexture);
        // Set particle shapeType (only on canvas render mode)
        if (this._emitter.setShapeType)
            this._emitter.setShapeType(cc.PARTICLE_STAR_SHAPE);

        this.setEmitterPosition();
        this.scheduleUpdate();
    }
});

FlowerTest.create = function() {
    var layer = new FlowerTest();
    return layer;
}