
var ProgressTimerHorizontal = cc.Layer.extend({
   init:function(){
       this._super();
       var winSize = cc.Director.getInstance().getWinSize();
       var to1 = cc.ProgressTo.create(5, 100);
       var to2 = cc.ProgressTo.create(5, 75);

       var left = cc.ProgressTimer.create(cc.Sprite.create(res.powered_png));
       left.setType(cc.PROGRESS_TIMER_TYPE_BAR);
       left.setPosition(200, winSize.height / 2);
       left.runAction(to1);
       //    Setup for a bar starting from the left since the midpoint is 0 for the x
       left.setMidpoint(cc.p(0, 0));
       //    Setup for a horizontal bar since the bar change rate is 0 for y meaning no vertical change
       left.setBarChangeRate(cc.p(1, 0));
       this.addChild(left);

       var right = cc.ProgressTimer.create(cc.Sprite.create(res.powered_png));
       right.setType(cc.PROGRESS_TIMER_TYPE_BAR);
       right.setReverseDirection(true);
       right.setPosition(winSize.width - 200, winSize.height / 2);
       right.runAction(to2);
       //    Setup for a bar starting from the left since the midpoint is 1 for the x
       right.setMidpoint(cc.p(1, 0));
       //    Setup for a horizontal bar since the bar change rate is 0 for y meaning no vertical change
       right.setBarChangeRate(cc.p(1, 0));
       this.addChild(right);
       return true;
   }
});
ProgressTimerHorizontal.create = function(args){
    var layer = new ProgressTimerHorizontal();
    return layer.init() ? layer : null;
}