var TMXHexTest = cc.Layer.extend({
    init : function(){
        this._super();

        var color = cc.LayerColor.create(cc.c4b(64, 64, 64, 255));
        this.addChild(color, -1);

        var map = cc.TMXTiledMap.create(res.hexa_test_tmx);
        this.addChild(map, 0, 1);
        return true;
    }
});

TMXHexTest.create = function(args){
    var layer = new TMXHexTest();
    return layer.init() ? layer : null;
};
