var TMXOrthoTest = cc.Layer.extend({
    init : function(){
        this._super();

        var map = cc.TMXTiledMap.create(res.orthogonal_test1_tmx);
        this.addChild(map, 0, 1);
        return true;
    }
});

TMXOrthoTest.create = function(args){
    var layer = new TMXOrthoTest();
    return layer.init() ? layer : null;
};
