var TMXOrthoTest2 = cc.Layer.extend({
    init : function(){
        this._super();

        var map = cc.TMXTiledMap.create(res.orthogonal_test2_tmx);
        this.addChild(map, 0, 1);
        return true;
    },
    onEnter:function () {
        this._super();
        cc.Director.getInstance().setProjection(cc.DIRECTOR_PROJECTION_3D);
    },
    onExit:function () {
        this._super();
        cc.Director.getInstance().setProjection(cc.DIRECTOR_PROJECTION_2D);
    }
});

TMXOrthoTest2.create = function(args){
    var layer = new TMXOrthoTest2();
    return layer.init() ? layer : null;
};