var TMXUncompressedTest = cc.Layer.extend({
    init : function(){
        this._super();

        var color = cc.LayerColor.create(cc.c4b(64, 64, 64, 255));
        this.addChild(color, -1);

        var map = cc.TMXTiledMap.create(res.iso_test2_uncompressed_tmx);
        this.addChild(map, 0, 1);

        // move map to the center of the screen
        var ms = map.getMapSize();
        var ts = map.getTileSize();
        map.setPosition(-ms.width * ts.width / 2, -ms.height * ts.height / 2)

        // testing release map
        var childrenArray = map.getChildren();
        var layer = null;
        for (var i = 0, len = childrenArray.length; i < len; i++) {
            layer = childrenArray[i];
            if (!layer)
                break;

            layer.releaseMap();
        }
        return true;
    }
});

TMXUncompressedTest.create = function(args){
    var layer = new TMXUncompressedTest();
    return layer.init() ? layer : null;
};
