var BoundingBoxTest = cc.Layer.extend({
    ctor : function(){
        this._super();

        var winSize = cc.Director.getInstance().getWinSize();
        var sprite = cc.Sprite.create(res.grossini_png);
        this.addChild(sprite);
        sprite.setPosition(winSize.width / 2, winSize.height / 2);
        var bb = sprite.getBoundingBox();
        logTest('BoundingBox:');
        //for( var i in bb )
        //    cc.log( i + " = " + bb[i] );
        logTest('origin = [ ' + bb.x + "," + bb.y + "]");
        logTest('size = [ ' + bb.width + "," + bb.height + "]");
        this.testBB = bb;
    }
});

BoundingBoxTest.create = function(args){
    var layer = new BoundingBoxTest();
    return layer.init() ? layer : null;
};
