var CCNodeTest2 = cc.Layer.extend({
    ctor : function(){
        this._super();

        var winSize = cc.Director.getInstance().getWinSize();
        var sp1 = cc.Sprite.create(res.grossinis_sister1_png);
        var sp2 = cc.Sprite.create(res.grossinis_sister2_png);
        var sp3 = cc.Sprite.create(res.grossinis_sister1_png);
        var sp4 = cc.Sprite.create(res.grossinis_sister2_png);

        sp1.setPosition(winSize.width / 4, winSize.height / 2);
        sp2.setPosition(winSize.width / 4 * 3, winSize.height / 2);
        this.addChild(sp1);
        this.addChild(sp2);

        sp3.setScale(0.25);
        sp4.setScale(0.25);

        sp1.addChild(sp3);
        sp2.addChild(sp4);
        sp2.setAnchorPoint(0, 0);
    }
});

CCNodeTest2.create = function(args){
    var layer = new CCNodeTest2();
    return layer.init() ? layer : null;
};
