var LabelTTFA8Test = cc.Layer.extend({
    ctor : function(){
        this._super();
        var winSize = cc.Director.getInstance().getWinSize();

        var layer = cc.LayerColor.create(cc.c4b(128, 128, 128, 255));
        this.addChild(layer, -10);

        var label1 = cc.LabelTTF.create("Testing A8 Format", "Arial", 48);
        this.addChild(label1);
        label1.setColor(cc.c3b(255, 0, 0));
        label1.setPosition(winSize.width / 2, winSize.height / 2);
    }
});

LabelTTFA8Test.create = function(args){
    var layer = new LabelTTFA8Test();
    return layer.init() ? layer : null;
};
