var LabelTTFMultiline = cc.Layer.extend({
    ctor : function(){
        this._super();
        var winSize = cc.Director.getInstance().getWinSize();

        var center = cc.LabelTTF.create("word wrap \"testing\" (bla0) bla1 'bla2' [bla3] (bla4) {bla5} {bla6} [bla7] (bla8) [bla9] 'bla0' \"bla1\"",
            "Arial", 32, cc.size(winSize.width / 2, 200), cc.TEXT_ALIGNMENT_CENTER, cc.VERTICAL_TEXT_ALIGNMENT_TOP);
        center.setPosition(winSize.width / 2, 150);
        this.addChild(center);
    }
});

LabelTTFMultiline.create = function(args){
    var layer = new LabelTTFMultiline();
    return layer.init() ? layer : null;
};
