var LayerGradientTest = cc.Layer.extend({
    ctor : function(){
        this._super();

        var layer1 = cc.LayerGradient.create(cc.c4b(255, 0, 0, 255), cc.c4b(0, 255, 0, 255), cc.p(0.9, 0.9));
        this.addChild(layer1, 0, 1);
        this.scheduleOnce(this.updateGradient, 2);
    },
    updateGradient:function() {
        var gradient = this.getChildByTag(1);
        gradient.setVector(cc.p(0.5,1));
    }
});

LayerGradientTest.create = function(args){
    var layer = new LayerGradientTest();
    return layer.init() ? layer : null;
};
