var Sprite1Test = cc.Layer.extend({
    init : function(){
        this._super();

        var winSize = cc.Director.getInstance().getWinSize();
        var idx = 0 | (Math.random() * 14);
        var x = (idx % 5) * 85;
        var y = (0 | (idx / 5)) * 121;
        var sprite = cc.Sprite.create(res.grossini_dance_atlas_png, cc.rect(x, y, 85, 121));
        this.addChild(sprite);
        sprite.setPosition(winSize.width/2, winSize.height/2);
        return true;
    }
});

Sprite1Test.create = function(args){
    var layer = new Sprite1Test();
    return layer.init() ? layer : null;
};