var GUIButtonTest = cc.LayerGradient.extend({
    init: function () {
        this._super(cc.c4b(98, 99, 117, 255), cc.c4b(0, 0, 0, 255));

        var uiLayer = ccs.UILayer.create();
        this.addChild(uiLayer);

        var button = ccs.Button.create();
        button.setTouchEnabled(true);
        button.loadTextures(res.buttonNormal_png, res.buttonPressed_png);
        button.setPosition(cc.p(400, 225));
        button.addTouchEventListener(this.touchEvent, this);
        uiLayer.addWidget(button);

        return true;
    },
    touchEvent: function (sender, type) {
        switch (type) {
            case ccs.TouchEventType.began:
                cc.log("Touch Down");
                break;
            case ccs.TouchEventType.moved:
                cc.log("Touch Move");
                break;
            case ccs.TouchEventType.ended:
                cc.log("Touch Up");
                break;
            case ccs.TouchEventType.canceled:
                cc.log("Touch Cancelled");
                break;
            default:
                break;
        }
    }
});

GUIButtonTest.create = function () {
    var layer = new GUIButtonTest();
    layer.init();
    return layer;
};