var GUICheckBoxTest = cc.LayerGradient.extend({
    init: function () {
        this._super(cc.c4b(98, 99, 117, 255), cc.c4b(0, 0, 0, 255));

        var uiLayer = ccs.UILayer.create();
        this.addChild(uiLayer);

        var checkBox = ccs.CheckBox.create();
        checkBox.setTouchEnabled(true);
        checkBox.loadTextures(res.check_box_normal_png,
            res.check_box_normal_press_png,
            res.check_box_active_png,
            res.check_box_normal_disable_png,
            res.check_box_active_disable_png);
        checkBox.setPosition(cc.p(400, 225));
        checkBox.addEventListenerCheckBox(this.selectedStateEvent, this);
        uiLayer.addWidget(checkBox);

        return true;
    },
    selectedStateEvent: function (sender, type) {
        switch (type) {
            case  ccs.CheckBoxEventType.unselected:
                cc.log("Unselected");
                break;
            case ccs.CheckBoxEventType.selected:
                cc.log("Selected");
                break;
            default:
                break;
        }
    }
});

GUICheckBoxTest.create = function () {
    var layer = new GUICheckBoxTest();
    layer.init();
    return layer;
};