
var GUIEditorTest = cc.LayerGradient.extend({
    init : function(){
        this._super(cc.c4b(98,99,117,255), cc.c4b(0,0,0,255));

        var uiLayer = ccs.UILayer.create();
        this.addChild(uiLayer);

        var widget = ccs.GUIReader.getInstance().widgetFromJsonFile(res.gui_json);
        widget.setPosition(cc.p(160,50));
        uiLayer.addWidget(widget);

        return true;
    }
});

GUIEditorTest.create = function(){
    var layer = new GUIEditorTest();
    layer.init();
    return layer;
};