
var SceneTest = cc.LayerGradient.extend({
    init : function(){
        this._super(cc.c4b(98,99,117,255), cc.c4b(0,0,0,255));

        var curNode = ccs.SceneReader.getInstance().createNodeWithSceneFile(res.FishJoy2_json);
        curNode.setPosition(cc.p(160,60));
        this.addChild(curNode);
        ccs.ActionManager.getInstance().playActionByName("startMenu_1.json", "Animation1");

        return true;
    }
});

SceneTest.create = function(){
    var layer = new SceneTest();
    layer.init();
    return layer;
};